mapper.select:

select 

from ssm_advice where 1=1 

    and adviceid = #{adviceid,jdbcType=INTEGER}


    and patientid = #{patientid,jdbcType=INTEGER}


    and doctorid = #{doctorid,jdbcType=INTEGER}


    and subjectid = #{subjectid,jdbcType=INTEGER}

mapper.insert:

insert into ssm_advice (adviceid, patientid, doctorid, 
  subjectid, advicecontent, advicedate, 
  adviceanswer, adviceanswerdate, isanswer
  )
values (#{adviceid,jdbcType=INTEGER}, #{patientid,jdbcType=INTEGER}, #{doctorid,jdbcType=INTEGER}, 
  #{subjectid,jdbcType=INTEGER}, #{advicecontent,jdbcType=VARCHAR}, #{advicedate,jdbcType=VARCHAR}, 
  #{adviceanswer,jdbcType=VARCHAR}, #{adviceanswerdate,jdbcType=VARCHAR}, #{isanswer,jdbcType=VARCHAR}
  )
